import React from 'react';
import { withFirebase } from '.';
import * as cache from '../lib/cache';
import { Router } from 'next/router';

const withAuthentication = Component => {
    class withAuthentication extends React.Component {
        constructor(props) {
            super(props);
            this.state = {
              authUser: null,
            }
        }

        componentDidMount() {
            this.listener = this.props.firebase.auth.onAuthStateChanged(authUser => {
              authUser
                ? this.ifAuthSaveToCache(authUser)
                : this.ifNotAuthRouteToSignIn()
            });
            // this.ifNotAuthRouteToSignIn();
        }


        ifNotAuthRouteToSignIn = () => {
            this.setState({
                authUser: null,
            });
            Router.push('/signin');
        }

        ifAuthSaveToCache = (authUser) => {
            this.setState({ authUser });
            return cache.saveToCache('uid', authUser.uid);
        }

        componentWillUnmount() {
            this.listener();
        }


        render() {
            const { authUser } = this.state;
            return <Component {...this.props} authUser={authUser}/>
        }
    }

    return withFirebase(withAuthentication);
};

export default withAuthentication;
