import React from 'react';
// import fetch from 'isomorphic-unfetch';
import { NextSeo } from 'next-seo';
import { compose } from 'redux';
import { connect } from 'react-redux';
import Router from 'next/router';
import {
  signInPageLoading,
  signInPageLoaded,
  signInPageFailed,
  saveUid
} from '../redux/actions';
import { Button, Input } from '../components';
import { withFirebase } from '../HOCs';
import * as cache from '../lib/cache';

import '../lib/index.css';

class SignInPage extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      submitting: false,
      error: null
    };
  }
  //   static async getInitialProps({ req }) {
  //     // const isServer = !!req
  //     const res = await fetch('https://jsonplaceholder.typicode.com/users');
  //     const json = await res.json();

  //     return {
  //       users: json
  //     };
  //   }

  componentDidMount() {
    const { pageLoading } = this.props;
    pageLoading();
    console.log('props ', this.props);
  }

  onSubmit = () => {
    const { email, password } = this.state;

    console.log('email ', email);
    console.log('password ', password);

    this.setState({
      submitting: true
    });
    this.props.firebase
      .doSignInWithEmailAndPassword(email, password)
      .then(res => {
        const { uid } = res.user;
        cache.saveToCache('uid', uid);
        this.setState({ email: '', password: '' });
        Router.push('/home');
      })
      .catch(error => {
        this.setState({ error });
      });
  };

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const { submitting, error } = this.state;
    console.log('this state ', this.state);
    return (
      <div id="page-container">
        <NextSeo
          openGraph={{
            type: 'website',
            url: 'https://www.thePandaRiot.com/gigs',
            title: 'Sign In',
            description: 'Sign in to the panda riot open mic comedy webapp!',
            images: [
              {
                url: 'https://i.ytimg.com/vi/kQBHzHBMlM4/hqdefault.jpg',
                width: 800,
                height: 600,
                alt: 'Og Image Alt'
              },
              {
                url:
                  'https://pbs.twimg.com/profile_images/498909008292347904/8EkJ3yZ-_400x400.png',
                width: 800,
                height: 600,
                alt: 'Og Image Alt 2'
              }
            ]
          }}
        />
        <h1 className="funky-title">Sign In: </h1>
        <Input
          name="email"
          title="email"
          onChange={this.onChange}
          placeholder="abc@abc.com"
        />
        <Input
          name="password"
          title="password"
          onChange={this.onChange}
          placeholder="password here"
        />
        <Button text="Submit" onClick={this.onSubmit} color="grey" />

        {submitting && <p>Signing In..</p>}
        {error && <p>{error.message}</p>}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  loading: state.signIn.loading,
  error: state.signIn.error
});

const mapDispatchToProps = dispatch => ({
  pageLoading: () => dispatch(signInPageLoading()),
  pageLoaded: () => dispatch(signInPageLoaded()),
  pageFailed: () => dispatch(signInPageFailed()),
  updateStateSaveUid: uid => dispatch(saveUid(uid))
});

export default compose(
  withFirebase,
  connect(
    mapStateToProps,
    mapDispatchToProps
  )
)(SignInPage);
